import{_ as e,o as a,c as t,R as r}from"./chunks/framework.7FlijoJG.js";const u=JSON.parse('{"title":"Hello，Gradle","description":"","frontmatter":{"title":"Hello，Gradle","date":"2023-11-17T18:05:10.000Z","tags":["tech","hello","gradle"],"draft":false,"hideInList":false,"feature":null,"isTop":false},"headers":[],"relativePath":"interview/Gradle.md","filePath":"interview/Gradle.md","lastUpdated":1703254130000}'),l={name:"interview/Gradle.md"},o=r('<h2 id="下载" tabindex="-1">下载 <a class="header-anchor" href="#下载" aria-label="Permalink to &quot;下载&quot;">​</a></h2><p><a href="https://gradle.org/install/#manually" target="_blank" rel="noreferrer">gradle官方网站</a></p><p>点击上方连接进入到官方网</p><p>手动安装的话，点击releases page 选择自己想要安装的包下载</p><p>我安装的是6.5，选择binary-only下载，下载好了之后解压放在自己想要放的位置，我这里放在的是/Users/butcher/code/tools/gradle-6.5/bin</p><h2 id="配置环境变量" tabindex="-1">配置环境变量 <a class="header-anchor" href="#配置环境变量" aria-label="Permalink to &quot;配置环境变量&quot;">​</a></h2><p>1.通过命令打开环境编辑文档，输入i进入编辑模式</p><p><code>vim ~/.bash_profile</code></p><p>2.在文档的尾部追加gradle的环境配置</p><p>`export PATH=$PATH:/Users/butcher/code/tools/gradle-6.5/bin</p><p>英文状态下，按esc键，然后输入<code>:wq</code>保存并退出文档</p><p>3.让配置的文档生效</p><p><code>source ~/.bash_profile</code></p><p>4.验证gradle安装成功</p><p><code>gradle -v</code></p><p>出现gradle的版本信息证明安装成功。</p><p><img src="https://bestkxt.oss-cn-guangzhou.aliyuncs.com/img/202311171858040.png" alt="image.png"></p><h2 id="idea-配置" tabindex="-1">idea 配置 <a class="header-anchor" href="#idea-配置" aria-label="Permalink to &quot;idea 配置&quot;">​</a></h2><p>加载 spring 源码需要这么配置</p><p><img src="https://bestkxt.oss-cn-guangzhou.aliyuncs.com/img/202311181237703.png" alt="image.png"></p>',20),s=[o];function p(d,i,n,c,h,_){return a(),t("div",null,s)}const m=e(l,[["render",p]]);export{u as __pageData,m as default};
