import{_ as e,o,c as t,R as c}from"./chunks/framework.7FlijoJG.js";const u=JSON.parse('{"title":"Hello，cron","description":"","frontmatter":{"title":"Hello，cron","date":"2023-10-04T20:48:29.000Z","tags":["tech","hello"],"draft":false,"hideInList":false,"feature":null,"isTop":false},"headers":[],"relativePath":"interview/cron.md","filePath":"interview/cron.md","lastUpdated":1703254130000}'),l={name:"interview/cron.md"},i=c('<p>[github action](github action.md)中可以配置<a href="./schedule.html">schedule</a>，而如果有定时或者重复构建的需求的话，就需要用到<a href="./cron.html">cron</a>。</p><p>cron表达式总共有五位，从左到右分别代表 分钟、小时、日期、月份、星期几。</p><p>分钟范围：0~59 小时范围：0~24 日期：1~31 月份：1~12 星期几： 0~6</p><p>空格意义：分隔不同的时间单位 逗号意义：同个时间范围里面的多个值 斜杠含义：代表每隔一段时间就重复 问号意义：不关心当前的时间单位 星号含义：任意</p><h3 id="案例" tabindex="-1">案例 <a class="header-anchor" href="#案例" aria-label="Permalink to &quot;案例&quot;">​</a></h3><ol><li>每天的凌晨 3 点执行：<code>0 3 * * *</code></li><li>每周一的上午 10 点执行：<code>0 10 * * 1</code></li><li>每个月的第一天执行：<code>* * 1 * *</code></li><li>每个星期五的下午 4 点执行：<code>* 16 * * 5</code></li></ol><ul><li>每隔 5 分钟执行任务：<code>*/5 * * * *</code></li><li>每隔 2 小时执行任务：<code>* */2 * * *</code></li><li>每天的上午 9 点和下午 3 点执行任务：<code>* 9,15 * * *</code></li><li>每月的第一天执行任务，不关心星期几：<code>* * 1 * ?</code></li><li>每周一和周五的上午 10 点执行任务，不关心日期：<code>* 10 * * 1,5 ?</code></li></ul>',7),a=[i];function d(r,n,s,_,h,p){return o(),t("div",null,a)}const m=e(l,[["render",d]]);export{u as __pageData,m as default};
